# TLE on 84th case. More work needed.
from typing import List, Set, Tuple
import sys, collections
from time import perf_counter
class Solution:
    def shortestBridge(self, A: List[List[int]]) -> int:
        dirs = [-1,0,1,0,-1]
        ROWS, COLS = len(A), len(A[0])
        def bfs(boundary_set, island_set):
            queue = collections.deque(boundary_set)
            tried = set()
            # The queue blows up with the increasing iterations.
            while len(queue) != 0:
                r,c = queue.popleft()
                # THIS WAS WHERE THINGS WENT WRONG.
                # In BFS, where to record the visited points matters!
                # They should be recorded right where they were initially unpacked from parent nodes. NOT WHEN THEY WERE PROCESSED AS PARENT NODE.
                # Because brother nodes don't see the record in the second situation, there could be chances they unpack each other and blow up the queue. (And they would block the depth from going further).
                # And bottom line, THERE SHOULD NOT BE REPETITIVE NODES IN THE STACK OR QUEUE.
                # In DFS, this is less of a problem. Brothers won't be unpacking nodes on the same level, cuasing blowup of stack. 
                # tried.add((r,c))
                for i in range(4):
                    x = r + dirs[i]
                    y = c + dirs[i+1]
                    if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in island_set and (x,y) not in tried: 
                        if A[x][y] == 1:
                            min_dist = sys.maxsize
                            for row, col in boundary_set:
                                min_dist = min(min_dist, abs(row-x) + abs(col-y))
                            return min_dist - 1
                        else:
                            queue.append([x,y])
                            # The nodes should be registered here, right when they were born, to avoid trampoline birth problem.
                            tried.add((x,y))

        island = set()
        path = set()
        bdry = set()
        for row in range(ROWS):
            for col in range(COLS):
                if A[row][col] == 1:
                    stack = [[row, col]]
                    while len(stack) != 0:
                        r, c = stack.pop()
                        path.add((r,c))
                        island.add((r,c))
                        for i in range(4):
                            x = r + dirs[i]
                            y = c + dirs[i+1]
                            if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in path:
                                if A[x][y] == 1:
                                    stack.append([x,y])
                                else:
                                    bdry.add((r,c))
                    
                    return bfs(bdry, island)



def shortestBridge(A: List[List[int]]) -> int:    
    m = len(A)
    n = len(A[0])

    directions = [(-1,0), (1,0), (0,-1), (0,1)]
    seen = set()

    for i in range(m):
        for j in range(n):
            if A[i][j] == 1:
                queue = collections.deque([(i,j)])
                seen.add((i,j))
                # traverse first island
                while queue:
                    cur_i, cur_j = queue.popleft()

                    for d in directions:
                        new_i = cur_i + d[0]
                        new_j = cur_j + d[1]

                        if 0 <= new_i < m and 0 <= new_j < n and A[new_i][new_j] == 1 and (new_i, new_j) not in seen:
                            queue.append((new_i, new_j))
                            seen.add((new_i, new_j))

                # expand the first island:        
                queue = collections.deque(seen)
                distance = 0
                print(len(queue))

                while queue:
                    for _ in range(len(queue)):
                        cur_i, cur_j = queue.popleft()

                        # distance > 0 to make sure this 1 is not the one from first island
                        if A[cur_i][cur_j] == 1 and distance > 0:
                            return distance - 1

                        for d in directions:
                            new_i = cur_i + d[0]
                            new_j = cur_j + d[1]

                            if 0 <= new_i < m and 0 <= new_j < n and (new_i, new_j) not in seen:
                                queue.append((new_i, new_j))
                                seen.add((new_i, new_j))

                    distance += 1


class Solution2:
    def shortestBridge(self, A: List[List[int]]) -> int:
        dirs = [-1,0,1,0,-1]
        ROWS, COLS = len(A), len(A[0])
        island = set()

        for row in range(ROWS):
            for col in range(COLS):
                if A[row][col] == 1:
                    stack = [(row, col)]
                    while len(stack) != 0:
                        r, c = stack.pop()
                        island.add((r,c))
                        for i in range(4):
                            x = r + dirs[i]
                            y = c + dirs[i+1]
                            if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in island and A[x][y] == 1:
                                stack.append((x,y))

                    queue = collections.deque(island)
                    dist = 0
                    while len(queue) != 0:
                        for _ in range(len(queue)):
                            r,c = queue.popleft()
                            for i in range(4):
                                x = r + dirs[i]
                                y = c + dirs[i+1]
                                if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in island: 
                                    queue.append((x,y))
                                    island.add((x,y))
                                    if A[x][y] == 1:
                                        return dist
                        dist += 1
                                    
class Solution3:
    def shortestBridge(self, A: List[List[int]]) -> int:
        dirs = [-1,0,1,0,-1]
        ROWS, COLS = len(A), len(A[0])
        island = set()
        boundary = set()

        for row in range(ROWS):
            for col in range(COLS):
                if A[row][col] == 1:
                    stack = [(row, col)]
                    while len(stack) != 0:
                        r, c = stack.pop()
                        island.add((r,c))
                        for i in range(4):
                            x = r + dirs[i]
                            y = c + dirs[i+1]
                            if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in island:
                                if A[x][y] == 1:
                                    stack.append((x,y))
                                elif A[x][y] == 0:
                                    boundary.add((r,c))

                    # The problem is not in using the boundary points. We would end up saving some iterations.
                    queue = collections.deque(boundary)
                    dist = 0
                    while len(queue) != 0:
                        for _ in range(len(queue)):
                            r,c = queue.popleft()
                            for i in range(4):
                                x = r + dirs[i]
                                y = c + dirs[i+1]
                                # But here in the condition, it is the island instead of the boundary that whether (x,y) is included should be checked. Otherwise the boundary wonn't stop boundary from growing points backwards.
                                if x not in [-1, ROWS] and y not in [-1, COLS] and (x,y) not in island: 
                                    queue.append((x,y))
                                    island.add((x,y))
                                    if A[x][y] == 1:
                                        return dist
                        dist += 1                   


